<div id="modelViewerControls" class="controls">
    <input id="colorInput" type="color" value="#1083ff"/>
    <div class="ranger" data-label="exposure"><input id="exposureInput" type="range" min="0" max="3" step=".01" value="1"></div>
    <div class="ranger" data-label="scale"><input id="scaleInput" type="range" min=".1" max="10" step="0.01" value="1"></div>
    <div class="ranger" data-label="roll"><input id="rollInput" type="range" min="0" max="5" step=".1" value="2.5"></div>
    <div class="ranger" data-label="pitch"><input id="pitchInput" type="range" min="-25" max="25" step="1" value="2.5"></div>
    <div class="ranger" data-label="yaw"><input id="yawInput" type="range" min="-25" max="25" step=".1" value="0"></div>
    <div class="ranger" data-label="metal"><input id="metalInput" type="range" min="0" max="1" step="0.01" value=".5"></div>
    <div class="ranger" data-label="roughness"><input id="roughInput" type="range" min="0" max="1" step="0.01" value=".5"></div>
    <div id="downloadPosterButton"></div>
</div>
